import axios from "axios";
import React, { useEffect, useState } from 'react';
import Card from "./Card";

const Countries = () => {
    const [data, setData] = useState([]);
    const [rangeValue, setRangeValue] = useState(36);
    const radio = ["Africa", "America", "Asia", "Europe", "Oceania"];
    const [selectedRadio, setSelectedRadio] = useState("")


    useEffect(() => {
        axios
            .get("https://restcountries.com/v3.1/all")
            .then(res => setData(res.data));

    },[]);


    return (
        <div className="countries">
            <ul className="radio-container">
                <input 
                    type="range" 
                    min="1" 
                    max="250" 
                    defaultValue={rangeValue} 
                    onChange={(e) => setRangeValue(e.target.value)} 
                />
                {RadioNodeList.map(continent => 
                    <li>
                        <input 
                            type="radio"
                            id={continent}
                            name="continentRadio"
                            checked={continent === selectedRadio}
                            onChange={e => setSelectedRadio(e.target.id)}
                        />
                        <label htmlFor={continent}>
                            {continent}
                        </label>
                    </li>
                )}
            </ul>
                {selectedRadio && 
                    <button onClick={() => setSelectedRadio("")}>Annuler la recherche</button>
                }
            <ul>
                {data
                    .filter(country => country.continent[0].include(selectedRadio))
                    .sort((a,b)=> b.population -a .population)
                    .slice(0, rangeValue)
                    .map((country, index) => <Card ley={index} country={country} />)}

            </ul>
        </div>
    );
};

export default Countries;
